            htmlString += '<div style="display: inline-block; padding: 5px; width: 18%; text-align: center; vertical-align: top;">';
            htmlString += '<div style="position: relative; width: 64px; margin: 0px auto;">';
            htmlString += '<img src="http://s01.tosbase.com/img/icons/skills/icon_' + skillObj.icon + '.png" style="height: 64px;" /><br/>';
            htmlString += '<div style="position: absolute; right: 2px; bottom: 2px;">';
            htmlString += '<span style="color: white; font-weight: bold; text-shadow: -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000, 1px 1px 0 #000;" id="skill_sim_skill_value_' + rank + '_' + skillName + '" class="skill_sim_skill_value " data-level="0" data-max-level="' + maxDisplayLevel + '">' + absoluteLevel + '</span>';
            htmlString += '<span style="color: white; font-weight: bold; text-shadow: -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000, 1px 1px 0 #000;"> / ' + maxDisplayLevel + '</span>';
            htmlString += '</div>';
            //
            htmlString += '<div style="position: absolute; top: 0px; right: 0px; bottom: 0px; left: 0px; cursor: pointer;"  data-tooltip="skill_sim_skill_' + skillName + '_' + circle + '_tooltip" class="skill_sim_tooltip_trigger" onclick="increaseSkill(' + rank + ', \'' + skillName + '\', 1);" oncontextmenu="decreaseSkill(' + rank + ', \'' + skillName + '\', 1); return false;">';
            htmlString += '</div>';
            //
            if(skillObj.useOverHeat === 1) {
                htmlString += '<div style="position: absolute; top: 2px; left: 3px;">';
                htmlString += '<span style="color: yellow; font-weight: bold; text-shadow: -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000, 1px 1px 0 #000; cursor: default;">' + skillObj.overHeatCount + '</span>';
                htmlString += '</div>';
            }
            //
            htmlString += '</div>';
            htmlString += '<a href="database/skills/' + skillObj.id + '/" target="_blank"><b>' + skillObj.name + '</b></a><br/>';
            htmlString += '<input id="skill_sim_skill_input_' + rank + '_' + skillName + '"  class="skill_sim_skill_input" type="hidden" name="skill_sim_skill_input_' + rank + '_' + skillName + '" value="0" />';
            //htmlString += 'Level <span id="skill_sim_skill_value_' + rank + '_' + skillName + '" class="skill_sim_skill_value " data-level="0" data-max-level="' + maxDisplayLevel + '">' + absoluteLevel + '</span> / ' + maxDisplayLevel + '<br/>';
            if(skillObj.video !== "") {
                htmlString += '<a class="link_button" style="font-size: 0.8em; min-width: 70px;" onclick="showSkillVideo2(\'' + skillObj.video + '\');return false;">Video</a>';
            }
            
            htmlString += '<div style="margin-top: 5px;">';
            if(skillAbilityList[skillName] !== undefined) {
                abilityNameList = skillAbilityList[skillName];
                for(j = 0; j < abilityNameList.length; ++j) {
                    abilityName = abilityNameList[j];
                    ability = abilityList[abilityName];
                    if(ability.ReqCircle <= circle) {
                        htmlString += '<a href="http://www.tosbase.com/database/attributes/' + ability.ClassID + '/" target="_blank"><img src="http://s01.tosbase.com/img/icons/skills/' + ability.Icon + '.png" style="height: 32px; width: 32px;" data-tooltip="skill_sim_ability_' + abilityName + '_tooltip" class="skill_sim_tooltip_trigger" alt="' + ability.Name + '" /></a> ';
                    }
                }
            }
            htmlString += '</div>';
            
            
            htmlString += '<div id="skill_sim_skill_' + skillName + '_' + circle + '_tooltip" style="display: none;">';
            htmlString += createSkillTooltipContent(skillObj, absoluteLevel);
            htmlString += '</div>';
            
            htmlString += '</div>';
        }
    }
    
    htmlString += ' <div style="text-align: right; font-weight: bold;"><span class="skill_sim_skill_points_' + className + '_' + circle + '">0</span> Puntos de habilidad</div>';
    
    htmlString += '<input type="hidden" name="selected_class_rank' + rank + '" value="' + className + '" />';
    
    $('#skill_sim_rank' + rank + '_skills').html(htmlString);
    $('#skill_sim_selected_ranks').val(rank);
    
    if(circle > 1) {
