    }
    
    //$('.skill_sim_skill_points_' + className).html(skillPoints[className]);
    updateSkillPointViews(className);
}

function escapeRegExp(string) {
    return string.replace(/([.*+?^=!:${}()|\[\]\/\\])/g, "\\$1");
}

function replaceAll(string, find, replace) {
  return string.replace(new RegExp(escapeRegExp(find), 'g'), replace);
}

function createSkillTooltipContent(skillObj, level) {
    var htmlString = "";
    
    htmlString += '<b>' + skillObj.name + '</b><br/><br/>';
    
    htmlString += skillObj.desc + '<br/><br/>';
    
    if(skillObj.useOverHeat === 1) {
        htmlString += '<b>Overheat:</b> ' + skillObj.overHeatCount + ' Charges<br/><br/>';
    }
    
    var desc2 = "";
    if(level > 0) {
        htmlString += "<b>Level " + (level) + "</b><br/>";
        var levelInfo = skillObj.levelList[level];
        desc2 = skillObj.desc2;
        
        desc2 = replaceAll(desc2, '#{SkillAtkAdd}#', levelInfo['#{SkillAtkAdd}#']);
        desc2 = replaceAll(desc2, '#{SkillFactor}#', levelInfo['#{SkillFactor}#']);
        desc2 = replaceAll(desc2, '#{SpendSP}#', levelInfo['#{SpendSP}#']);
        desc2 = replaceAll(desc2, '#{SpendPoison}#', levelInfo['#{SpendPoison}#']);
        desc2 = replaceAll(desc2, '#{SkillSR}#', levelInfo['#{SkillSR}#']);
        desc2 = replaceAll(desc2, '#{CaptionTime}#', levelInfo['#{CaptionTime}#']);
        desc2 = replaceAll(desc2, '#{CaptionRatio}#', levelInfo['#{CaptionRatio}#']);
        desc2 = replaceAll(desc2, '#{CaptionRatio2}#', levelInfo['#{CaptionRatio2}#']);

    
        htmlString += desc2;
        htmlString += '<br/><b>SP:</b> ' + levelInfo['sp'] + '<br/>';
        htmlString += '<br/>';
    }
    
    if(level < skillObj.maxLevel) {
        htmlString += "<b>Level " + (level + 1) + "</b><br/>";
        var levelInfo = skillObj.levelList[level + 1];
        desc2 = skillObj.desc2;
        
        desc2 = replaceAll(desc2, '#{SkillAtkAdd}#', levelInfo['#{SkillAtkAdd}#']);
        desc2 = replaceAll(desc2, '#{SkillFactor}#', levelInfo['#{SkillFactor}#']);
        desc2 = replaceAll(desc2, '#{SpendSP}#', levelInfo['#{SpendSP}#']);
        desc2 = replaceAll(desc2, '#{SpendPoison}#', levelInfo['#{SpendPoison}#']);
        desc2 = replaceAll(desc2, '#{SkillSR}#', levelInfo['#{SkillSR}#']);
        desc2 = replaceAll(desc2, '#{CaptionTime}#', levelInfo['#{CaptionTime}#']);
        desc2 = replaceAll(desc2, '#{CaptionRatio}#', levelInfo['#{CaptionRatio}#']);
        desc2 = replaceAll(desc2, '#{CaptionRatio2}#', levelInfo['#{CaptionRatio2}#']);
    
        htmlString += desc2;
        htmlString += '<br/><b>SP:</b> ' + levelInfo['sp'] + '<br/>';
        htmlString += '<br/>';
    }
    
    htmlString += "<b>Type:</b> " + skillObj.type1 + " / " + skillObj.type2 + "<br/>";
    
    htmlString += "<b>Cooldown:</b> " + skillObj.cooldown + " seconds<br/>";
    
    
    
    
    if(skillObj.element !== "Melee") {
        htmlString += "<b>Element:</b> ";
        htmlString += '<img src="http://www.tosbase.com/content/img/icons/elements/' + skillObj.element.toLowerCase() + '.png"  alt="' + skillObj.element + '" style="vertical-align: middle;"/><br/>';
    }
    
    if(skillObj.reqStance !== "") {
        htmlString += "<b>Required Stance:</b> " + skillObj.reqStance + '<br/>';
    }
    
    htmlString += '<b>Companion:</b> <img src="http://www.tosbase.com/design/img/tooltips/skills/' + skillObj.EnableCompanion + '.png" alt="companion" style="vertical-align: middle;" /><br/>';
    
    return htmlString;
}

function updateSkillTooltip(skillObj, circle, level) {
    var htmlString = createSkillTooltipContent(skillObj, level);
    
    $('#skill_sim_skill_' + skillObj.ident + '_' + circle + '_tooltip').html(htmlString);
    $('#tooltip').html(htmlString);
}

function increaseSkill(rank, skillName, levels) {
    className = selectedClasses[rank - 1];
    
    skillElement = $('#skill_sim_skill_value_' + rank + '_' + skillName);
    
    if(skillElement.length === 0) {
        return;
    }
    
    skillLevel = skillElement.data('level');
    skillLevelAbsolute = parseInt(skillElement.html());
    skillMaxLevel = skillElement.data('max-level');
    
    classSkillPoints = skillPoints[selectedClasses[rank - 1]];
    realSkillPoints = getRealSkillPoints(rank, className);
    
    if(skillLevel === skillMaxLevel) {
        decreaseSkill(rank, skillName, skillLevel);
    } else if(skillLevelAbsolute >= skillMaxLevel) {
        decreaseSkill(rank, skillName, skillLevel);
    } else if(realSkillPoints === 0) {
        //not enough skill points
    } else {
        skillLevel += levels;
        skillLevelAbsolute += levels;
        spentSkillPoints[rank - 1] += levels;
        skillElement.data('level', skillLevel);
        skillElement.html(skillLevelAbsolute);
        
        $('#skill_sim_skill_input_' + rank + '_' + skillName).val(skillLevel);
        
        skillPoints[selectedClasses[rank - 1]] = classSkillPoints - levels;
        //$('.skill_sim_skill_points_' + className).html(skillPoints[selectedClasses[rank - 1]]);
        updateSkillPointViews(className);
        
        updateSkillTooltip(skillList[skillName], classCircleList[className], skillLevelAbsolute);
        
        //if(rank < maxRank && selectedClasses[rank - 1] === selectedClasses[rank]) {
        if(rank < maxRank) {
            increaseSkillUpper(rank + 1, className, skillName, levels);
        }
    }
}

function increaseSkillUpper(rank, className, skillName, levels) {
    if(selectedClasses[rank - 1] === className) {
        skillElement = $('#skill_sim_skill_value_' + rank + '_' + skillName);
        skillLevel = skillElement.data('level');
        skillMaxLevel = skillElement.data('max-level');
        skillLevelAbsolute = parseInt(skillElement.html());

        if(skillLevelAbsolute >= skillMaxLevel) {
            skillLevelDiff = (skillLevelAbsolute - skillMaxLevel) + levels;
            skillLevel -= skillLevelDiff;
            spentSkillPoints[rank - 1] -= skillLevelDiff;
            skillElement.data('level', skillLevel);

            $('#skill_sim_skill_input_' + rank + '_' + skillName).val(skillLevel);

            className = selectedClasses[rank - 1];
            skillPoints[className] += skillLevelDiff;
            //$('.skill_sim_skill_points_' + className).html(skillPoints[className]);
            updateSkillPointViews(className);
        } else {
            skillElement.html(skillLevelAbsolute + levels);

            updateSkillTooltip(skillList[skillName], classCircleList[className], skillLevelAbsolute + levels);

            /*if(rank < maxRank && selectedClasses[rank - 1] === selectedClasses[rank]) {
                increaseSkillUpper(rank + 1, skillName, levels);
            }*/
        }
    }
    
    if(rank < maxRank) {
        increaseSkillUpper(rank + 1, className, skillName, levels);
    }
}


function decreaseSkill(rank, skillName, levels) {
    className = selectedClasses[rank - 1];
    
    skillElement = $('#skill_sim_skill_value_' + rank + '_' + skillName);
    skillLevel = skillElement.data('level');
    skillLevelAbsolute = parseInt(skillElement.html());
    
    classSkillPoints = skillPoints[selectedClasses[rank - 1]];
    
    if(skillLevel === 0) {
        skillMaxLevel = skillElement.data('max-level');
        var incLevels = Math.min((skillMaxLevel - skillLevelAbsolute), getRealSkillPoints(rank, className)); //classSkillPoints);
        increaseSkill(rank, skillName, incLevels);
        return;
    } else {
        skillLevel -= levels;
        skillLevelAbsolute -= levels;
        spentSkillPoints[rank - 1] -= levels;
        skillElement.data('level', skillLevel);
        skillElement.html(skillLevelAbsolute);
        
        $('#skill_sim_skill_input_' + rank + '_' + skillName).val(skillLevel);
        
        skillPoints[selectedClasses[rank - 1]] = classSkillPoints + levels;
        //$('.skill_sim_skill_points_' + className).html(skillPoints[selectedClasses[rank - 1]]);
        updateSkillPointViews(className);
        
        updateSkillTooltip(skillList[skillName], classCircleList[className], skillLevelAbsolute);
        
        //if(rank < maxRank && selectedClasses[rank - 1] === selectedClasses[rank]) {
        if(rank < maxRank) {
            decreaseSkillUpper(rank + 1, className, skillName, levels);
        }
    }
}


function decreaseSkillUpper(rank, className, skillName, levels) {
    if(selectedClasses[rank - 1] === className) {
        skillElement = $('#skill_sim_skill_value_' + rank + '_' + skillName);
        skillLevel = skillElement.data('level');
        skillLevelAbsolute = parseInt(skillElement.html());

        if(skillLevelAbsolute > 0) {
            skillElement.html(skillLevelAbsolute - levels);

            updateSkillTooltip(skillList[skillName], classCircleList[className], skillLevelAbsolute - levels);
        }
    }
    
    if(rank < maxRank) {
        decreaseSkillUpper(rank + 1, className, skillName, levels);
    }
}

function updateSkillPointViews(className) {
    for(rank = 1; rank <= maxRank; ++rank) {
        if(selectedClasses[rank - 1] !== className) continue;
        
        $('.skill_sim_skill_points_' + className + '_' + circleList[rank - 1]).html(getRealSkillPoints(rank, className));
    }
}

function getRealSkillPoints(rank, className) {
    //value = Math.min(skillPoints[className], (circleList[rank - 1] * 15) - spentSkillPoints[rank - 1]);
    maxCircleLevel = (circleList[rank - 1] * 15);
    if(rank >= 2 && selectedClasses[rank - 2] === selectedClasses[rank - 1]) {
        maxCircleLevel -= spentSkillPoints[rank - 2];
    }
    if(selectedClasses[rank] === selectedClasses[rank - 1] && spentSkillPoints[rank] > 15) {
        maxCircleLevel -= (spentSkillPoints[rank] - 15);
    }
    
    value = Math.min(skillPoints[className], maxCircleLevel - spentSkillPoints[rank - 1]);
    
    return value;
}



$(document).on('click', '.save_button', function(evt) {
    if($('#skill_sim_selected_ranks').val() === '0' || selectedClasses[0] === "") {
        alert('Por favor, selecciona al menos una clase');
        evt.preventDefault();
    }
});

$(document).on('click', '.preview_button', function(evt) {
    desc = $('#skill_sim_description_text').val();
    if(desc !== '') {
        $.ajax({
            type: "POST",
            url: "http://www.tosbase.com/browserapi/skill-simulator/getDescriptionPreview.php",
            data: { description: desc },
            success: function(response){
                data = jQuery.parseJSON(response);
                if(data.status !== "success") {
                    alert('Error: ' + data.message);
                    return;
                }
                
                $('#skill_sim_preview_description_text').html(data.description);
                $("#skill_sim_preview").show();
                
                
                $('html, body').animate({
                    scrollTop: $("#skill_sim_preview").offset().top
                }, 500);

            },
            error:function(xhr){
                alert("Error: " + xhr.responseText);
            }
        });
    }
});

